CREATE OR REPLACE FUNCTION obter_conteudo_email_opme (nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ds_opme_w		varchar(2000);
ds_material_w		varchar(254);
qt_material_w		double precision;
ds_observacao_w		varchar(255);

c01 CURSOR FOR
	SELECT	substr(obter_desc_material(a.cd_material),1,254) ds_material,
		a.qt_material,
		substr(a.ds_observacao,1,255)
	from	agenda_pac_opme a
	where	a.nr_seq_agenda = nr_seq_agenda_p
	and	a.ie_origem_inf	= 'I'
	order by a.nr_seq_apres desc;


BEGIN

open c01;
loop
fetch c01 into
	ds_material_w,
	qt_material_w,
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ds_opme_w	:= substr(ds_material_w || ' (Qt: ' || qt_material_w || ')' || chr(13) || chr(10) || ds_observacao_w || chr(13) || chr(10) || ds_opme_w,1,2000);
	end;
end loop;
close c01;

ds_opme_w	:= 	substr('OPME: ' || chr(13) || chr(10) || ds_opme_w,1,2000);

return 	substr(ds_opme_w,1,2000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_conteudo_email_opme (nr_seq_agenda_p bigint) FROM PUBLIC;

