SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_conteudo_email_sangue (nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ds_sangue_w		varchar(2000);
ds_material_w		varchar(254);
qt_material_w		double precision;
ds_observacao_w		varchar(255);

c01 CURSOR FOR
	SELECT	SUBSTR(obter_desc_proc_interno(nr_seq_proc_interno),1,255),
		a.qt_bolsas_sangue,
		SUBSTR(obter_desc_status_sangue(nr_seq_status),1,255)
	FROM	agenda_pac_sangue a
	WHERE	a.nr_seq_agenda = nr_seq_agenda_p
	ORDER BY 1 DESC;


BEGIN

open c01;
loop
fetch c01 into
	ds_material_w,
	qt_material_w,
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ds_sangue_w	:= ds_material_w || ' (' || Wheb_mensagem_pck.get_texto(308875) ||': ' /*' (Qt: '*/ || qt_material_w || ')' || chr(13) || chr(10) || ds_observacao_w || chr(13) || chr(10) || ds_sangue_w;
	end;
end loop;
close c01;

ds_sangue_w	:= Wheb_mensagem_pck.get_texto(308890) || ': ' /*'Banco de Sangue: '*/ || chr(13) || chr(10) || ds_sangue_w;

return 	substr(ds_sangue_w,1,2000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
