SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_conteudo_email_servicos (nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ds_servicos_w		varchar(2000);
ds_material_w		varchar(254);
ie_status_w		varchar(255);

c01 CURSOR FOR
	SELECT	substr(obter_desc_proc_exame(nr_seq_proc_servico),1,100),
		substr(obter_valor_dominio(3195,IE_STATUS),1,255)
	FROM	agenda_pac_servico a
	WHERE	a.nr_seq_agenda = nr_seq_agenda_p
	and	coalesce(a.ie_origem_inf,'I') = 'I'
	ORDER BY 1;


BEGIN

open c01;
loop
fetch c01 into
	ds_material_w,
	ie_status_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ds_servicos_w	:= ds_material_w || ' - ' || Wheb_mensagem_pck.get_texto(308891) || ': ' /*' - Status: '*/ || ie_status_w || chr(13) || chr(10) || ds_servicos_w;
	end;
end loop;
close c01;

ds_servicos_w	:= Wheb_mensagem_pck.get_texto(308892) || ': ' /*'Servi√ßos: '*/ || chr(13) || chr(10) || ds_servicos_w;

return 	substr(ds_servicos_w,1,2000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
