SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_conteudo_itens_sem_cod (nr_seq_agenda_p bigint, ie_tipo_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_item_sem_cod_w	varchar(2000);
ds_material_w		varchar(255);
qt_material_w		double precision;

--ie_tipo_item_p: 1= Materiais / Medicamentos - 2= OPME
c01 CURSOR FOR
	SELECT	substr(a.ds_material,1,255) ds_material,
               a.qt_quantidade qt_material
	from	agenda_pac_desc_material a
	where	a.nr_seq_agenda = nr_seq_agenda_p
   and coalesce(ie_situacao, 'A') = 'A'
   and a.ie_tipo_item = ie_tipo_item_p
	order by a.ds_material desc;


BEGIN

open c01;
loop
fetch c01 into
	ds_material_w,
	qt_material_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ds_item_sem_cod_w	:= substr(ds_material_w || ' (Qt: ' || qt_material_w || ')' || chr(13) || chr(10) || ds_item_sem_cod_w,1,2000);
	end;
end loop;
close c01;

if (ie_tipo_item_p = 1) then
   ds_item_sem_cod_w	:= 	substr(obter_desc_expressao(778921) || chr(13) || chr(10) || ds_item_sem_cod_w,1,2000);
else
   ds_item_sem_cod_w	:= 	substr(obter_desc_expressao(778920) || chr(13) || chr(10) || ds_item_sem_cod_w,1,2000);
end if;

return 	substr(ds_item_sem_cod_w,1,2000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
