CREATE OR REPLACE FUNCTION obter_contrato_mensalidade (nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_mensalidade_w	bigint;
nr_seq_lote_w		bigint;
nr_seq_contrato_w	bigint;
nr_contrato_w		bigint;
ds_retorno_w		varchar(20);


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then

	select	max(a.nr_seq_mensalidade)
	into STRICT	nr_seq_mensalidade_w
	from	titulo_receber a
	where	a.nr_titulo		= nr_titulo_p;

	if (nr_seq_mensalidade_w IS NOT NULL AND nr_seq_mensalidade_w::text <> '') then
		select	distinct(d.nr_contrato)
		into STRICT	nr_contrato_w
		from	pls_contrato d,
			pls_segurado c,
			pls_mensalidade_segurado b,
			pls_mensalidade a
		where	a.nr_sequencia	= b.nr_seq_mensalidade
		and	c.nr_sequencia	= b.nr_seq_segurado
		and	d.nr_sequencia	= c.nr_seq_contrato
		and	a.nr_sequencia	= nr_seq_mensalidade_w;

			ds_retorno_w := 	nr_contrato_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
