CREATE OR REPLACE FUNCTION obter_contrato_regra_nf_qtde ( nr_seq_regra_nf_p bigint, dt_vigencia_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_material_w		double precision;
qt_registro_w		bigint;

BEGIN

select	coalesce(max(qt_material_fixa),1)
into STRICT	qt_material_w
from	contrato_regra_nf
where	nr_sequencia = nr_seq_regra_nf_p;

select	count(*)
into STRICT	qt_registro_w
from	contrato_regra_nf_qtde
where	nr_seq_regra_nf = nr_seq_regra_nf_p
and	((coalesce(dt_mesano_referencia::text, '') = '') or (dt_mesano_referencia = trunc(dt_vigencia_p,'mm')));

if (qt_registro_w > 0) then

	select	max(qt_material)
	into STRICT	qt_material_w
	from	contrato_regra_nf_qtde
	where	nr_seq_regra_nf = nr_seq_regra_nf_p
	and	((coalesce(dt_mesano_referencia::text, '') = '') or (dt_mesano_referencia = trunc(dt_vigencia_p,'mm')));
end if;

return	coalesce(qt_material_w,1);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
