CREATE OR REPLACE FUNCTION obter_controle_tab_perfil (nm_tabela_p tabela_sistema_perfil.nm_tabela%type, nm_usuario_p usuario.nm_usuario%type) RETURNS varchar AS $body$
DECLARE


ie_controle_w	tabela_sistema_perfil.ie_controle%type;


BEGIN

select	coalesce(max(a.ie_controle), 'T')
into STRICT	ie_controle_w
from	tabela_sistema_perfil a
where	a.nm_tabela	= nm_tabela_p
and	a.cd_perfil	in (SELECT	x.cd_perfil
	from	usuario_perfil x
	where	x.nm_usuario	= nm_usuario_p);

return	ie_controle_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
