CREATE OR REPLACE FUNCTION obter_cont_atual_inventario ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_item_cont_w		integer;
qt_item_recont_w		integer;
qt_item_seg_recont_w		integer;
qt_item_terc_recont_w	integer;
qt_retorno_w			smallint;



BEGIN

select	count(*)
into STRICT	qt_item_cont_w
from	inventario_material
where	nr_seq_inventario = nr_sequencia_p
and	coalesce(qt_contagem::text, '') = ''
and	coalesce(qt_inventario::text, '') = '';

select	count(*)
into STRICT	qt_item_recont_w
from	inventario_material
where	nr_seq_inventario = nr_sequencia_p
and	coalesce(qt_recontagem::text, '') = ''
and	coalesce(qt_inventario::text, '') = '';

select	count(*)
into STRICT	qt_item_seg_recont_w
from	inventario_material
where	nr_seq_inventario = nr_sequencia_p
and	coalesce(qt_seg_recontagem::text, '') = ''
and	coalesce(qt_inventario::text, '') = '';

select	count(*)
into STRICT	qt_item_terc_recont_w
from	inventario_material
where	nr_seq_inventario = nr_sequencia_p
and	coalesce(qt_terc_recontagem::text, '') = ''
and	coalesce(qt_inventario::text, '') = '';

if (qt_item_cont_w > 0) then
	qt_retorno_w := 1;
elsif (qt_item_recont_w > 0) then
	qt_retorno_w := 2;
elsif (qt_item_seg_recont_w > 0) then
	qt_retorno_w := 3;
elsif (qt_item_terc_recont_w > 0) then
	qt_retorno_w := 4;
else
	qt_retorno_w := 0;
end if;

return qt_retorno_w;

end;
$body$
