CREATE OR REPLACE FUNCTION obter_convenio_agenda ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* 
	c - codigo 
	d - nome do convenio 
*/
 
 
dt_agenda_ultima_w		timestamp;
cd_convenio_w			bigint;
ds_retorno_w			varchar(2000);

 
c01 CURSOR FOR 
	SELECT	cd_convenio, dt_agenda 
	from	agenda_paciente_v 
	where	cd_tipo_agenda IN (1,2) 
	and	cd_pessoa_fisica = cd_pessoa_fisica_p 
	and	(dt_agenda IS NOT NULL AND dt_agenda::text <> '') 
	and 	(cd_convenio IS NOT NULL AND cd_convenio::text <> '') 
	
union
 
	SELECT	cd_convenio, dt_agenda 
	from	agenda_consulta 
	where	cd_pessoa_fisica = cd_pessoa_fisica_p 
	and	(dt_agenda IS NOT NULL AND dt_agenda::text <> '') 
	and	(cd_convenio IS NOT NULL AND cd_convenio::text <> '') 
	order by dt_agenda;


BEGIN 
 
open c01;
	loop 
		fetch c01 into	 
			cd_convenio_w, 
			dt_agenda_ultima_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */	
			begin 
			cd_convenio_w := cd_convenio_w;
			end;		
	end loop;
close c01;
 
if (ie_opcao_p = 'C') then 
	ds_retorno_w := cd_convenio_w;
elsif (ie_opcao_p = 'D') then 
	ds_retorno_w := substr(obter_nome_convenio(cd_convenio_w),1,150);
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
