SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_convenio_ageturno_wlcb ( cd_convenio_p bigint, cd_agenda_p bigint, dt_encaixe_p text, hr_encaixe_p text) RETURNS varchar AS $body$
DECLARE


qt_regra_conv_w 	bigint;
ds_retorno_w		varchar(1)	:= 'N';
ie_dia_semana_ww	smallint;
nr_seq_turno_w		bigint;


BEGIN

select 	count(*)
into STRICT	qt_regra_conv_w
from	AGENDA_TURNO_CONV a
where	a.cd_agenda 	= cd_agenda_p
and	a.cd_convenio	= cd_convenio_p;

if (qt_regra_conv_w > 0) then --se convenio existir na agenda da tabela AGENDA_TURNO_CONV...
	select	obter_cod_dia_semana(dt_encaixe_p)
	into STRICT	ie_dia_semana_ww
	;

	select 	max(a.nr_seq_turno)
	into STRICT	nr_seq_turno_w
	from	agenda_turno_conv a,
		agenda_turno b
	where 	b.cd_agenda 	= a.cd_agenda
	and	b.nr_sequencia	= a.nr_seq_turno
	and	b.ie_dia_semana = ie_dia_semana_ww
	and	to_date('30/12/1899 '||hr_encaixe_p,'dd/mm/yyyy hh24:mi:ss') between b.hr_inicial and b.hr_final
	and	a.cd_agenda 	= cd_agenda_p
	and	a.cd_convenio	= cd_convenio_p
	and (dt_encaixe_p >= b.dt_inicio_vigencia	or coalesce(b.dt_inicio_vigencia::text, '') = '')
	and (dt_encaixe_p <= b.dt_final_vigencia	or coalesce(b.dt_final_vigencia::text, '') = '');

	if (coalesce(nr_seq_turno_w::text, '') = '') then --se vigencia is null entao pega dias de trabalho....
		select 	max(a.nr_seq_turno)
		into STRICT	nr_seq_turno_w
		from	agenda_turno_conv a,
			agenda_turno b
		where 	b.cd_agenda 	= a.cd_agenda
		and	b.nr_sequencia	= a.nr_seq_turno
		and 	b.ie_dia_semana = 9
		and	to_date('30/12/1899 '||hr_encaixe_p,'dd/mm/yyyy hh24:mi:ss') between b.hr_inicial and b.hr_final
		and	a.cd_agenda 	= cd_agenda_p
		and	a.cd_convenio	= cd_convenio_p;
	end if;

end if;


/* Turnos especiais */

select 	count(*)
into STRICT	qt_regra_conv_w
from	agenda_turno_esp_conv a
where	a.cd_agenda 	= cd_agenda_p
and	a.cd_convenio	= cd_convenio_p;

if (qt_regra_conv_w > 0 ) then --se convenio existir na agenda da tabela AGENDA_TURNO_CONV...
	select 	max(a.nr_seq_turno_esp)
	into STRICT	nr_seq_turno_w
	from	agenda_turno_esp_conv a,
		agenda_turno_esp b
	where 	b.cd_agenda 	= a.cd_agenda
	and	b.nr_sequencia	= a.nr_seq_turno_esp
	and	a.cd_agenda 	= cd_agenda_p
	and	a.cd_convenio	= cd_convenio_p
	and	to_date('30/12/1899 '||hr_encaixe_p,'dd/mm/yyyy hh24:mi:ss') between b.hr_inicial and b.hr_final
	and (dt_encaixe_p >= b.dt_agenda		or coalesce(b.dt_agenda::text, '') = '')
	and (dt_encaixe_p <= b.dt_agenda_fim	or coalesce(b.dt_agenda_fim::text, '') = '');

end if;


if 	(nr_seq_turno_w IS NOT NULL AND nr_seq_turno_w::text <> '')  then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_convenio_ageturno_wlcb ( cd_convenio_p bigint, cd_agenda_p bigint, dt_encaixe_p text, hr_encaixe_p text) FROM PUBLIC;

