CREATE OR REPLACE FUNCTION obter_convenio_atend_mx ( nr_atendimento_p bigint, nr_seq_ordem_conv_p bigint, ie_tipo_inf_p text default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
cd_convenio_w		atend_categoria_convenio.cd_convenio%type;
qt_reg_w			bigint;		

BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then	
	
	if (nr_seq_ordem_conv_p = 1) then
		select 	max(cd_convenio)
		into STRICT	cd_convenio_w
		from 	atend_categoria_convenio
		where 	nr_seq_interno = (	SELECT 	min(nr_seq_interno)
						from 	ATEND_CATEGORIA_CONVENIO 
						where 	nr_atendimento = nr_atendimento_p);
	else	
		select 	max(cd_convenio)
		into STRICT	cd_convenio_w
		from 	atend_categoria_convenio
		where 	nr_seq_interno = (	SELECT 	max(nr_seq_interno)
						from 	ATEND_CATEGORIA_CONVENIO 
						where 	nr_atendimento = nr_atendimento_p);
	end if;
	
	select	count(*)
	into STRICT	qt_reg_w
	from	atend_categoria_convenio
	where	nr_atendimento = nr_atendimento_p;
	
	if (ie_tipo_inf_p = 'CD_USUARIO_CONVENIO') then
		select	max(CD_USUARIO_CONVENIO)
		into STRICT	ds_retorno_w
		from	atend_categoria_convenio
		where	nr_atendimento = nr_atendimento_p
		and		cd_convenio = cd_convenio_w;
	else
		if (qt_reg_w <2) and (nr_seq_Ordem_conv_p >= 2)then
		ds_retorno_w := '88';
	else
		select 	coalesce(obter_dados_cat_derecho(cd_tipo_convenio_mx,coalesce(ie_tipo_inf_p,'CD_DERECHOHABIENCIA')),'99')
		into STRICT	ds_retorno_w
		from 	convenio
		where 	cd_convenio = cd_convenio_w;
	end if;	
	end if;
	
	
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
