CREATE OR REPLACE FUNCTION obter_convenio_demonstrativo (nr_seq_demonstrativo_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_conta_convenio_w	bigint;
cd_convenio_w		bigint;
ds_convenio_w		varchar(100) := null;


BEGIN 
 
select 	a.nr_lote 
into STRICT	nr_conta_convenio_w 
from 	tiss_dem_pag_resumo a 
where	a.nr_sequencia	= nr_seq_demonstrativo_p;
 
if (nr_conta_convenio_w IS NOT NULL AND nr_conta_convenio_w::text <> '') then 
 
	select 	max(a.cd_convenio_parametro) 
	into STRICT 	cd_convenio_w 
	from 	conta_paciente a 
	where	nr_conta_convenio 	= nr_conta_convenio_w;
	 
	if (cd_convenio_w IS NOT NULL AND cd_convenio_w::text <> '') then				 
		ds_convenio_w	:= substr(obter_nome_convenio(cd_convenio_w),1,100);
	end if;
 
end if;
 
return	ds_convenio_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
