CREATE OR REPLACE FUNCTION obter_convenio_estab ( cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


cd_convenio_w		convenio.cd_convenio%type;
cd_cgc_w		estabelecimento.cd_cgc%type;


BEGIN

if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then

	select	max(cd_cgc)
	into STRICT	cd_cgc_w
	from	estabelecimento
	where   cd_estabelecimento = cd_estabelecimento_p;

	select  max(cd_convenio)
	into STRICT 	cd_convenio_w
	from 	convenio
	where 	cd_cgc = cd_cgc_w
	and	ie_situacao = 'A';
end if;

return	cd_convenio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
