CREATE OR REPLACE FUNCTION obter_convenio_menor_prio ( nr_atendimento_p atend_categoria_convenio.nr_atendimento%type) RETURNS bigint AS $body$
DECLARE


cd_convenio_w	atend_categoria_convenio.cd_convenio%type;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '')then
	select	max(ac.cd_convenio)
	into STRICT	cd_convenio_w
	from 	atend_categoria_convenio ac
	where   ac.nr_atendimento = nr_atendimento_p
	and     ac.dt_inicio_vigencia <= clock_timestamp()
	and (coalesce(ac.dt_final_vigencia::text, '') = '' or ac.dt_final_vigencia > clock_timestamp())
	and     ac.nr_prioridade = (	SELECT  min(x.nr_prioridade)
					from    atend_categoria_convenio x
					where   x.nr_atendimento = ac.nr_atendimento
					and     x.dt_inicio_vigencia <= clock_timestamp()
					and (coalesce(x.dt_final_vigencia::text, '') = '' or x.dt_final_vigencia > clock_timestamp()));
end if;

return cd_convenio_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
