CREATE OR REPLACE FUNCTION obter_convenio_motivo_glosa ( cd_convenio_p bigint, cd_glosa_convenio_p text) RETURNS varchar AS $body$
DECLARE


cd_motivo_w		varchar(255)	:= null;


BEGIN

if (cd_glosa_convenio_p IS NOT NULL AND cd_glosa_convenio_p::text <> '') then

	select	coalesce(max(to_char(cd_motivo_glosa)), cd_glosa_convenio_p)
	into STRICT	cd_motivo_w
	from	convenio_motivo_glosa
	where	cd_convenio			= cd_convenio_p
	and	to_char(cd_glosa_convenio)		= coalesce(cd_glosa_convenio_p,'X');

end if;

RETURN cd_motivo_w;

END;
$body$
