CREATE OR REPLACE FUNCTION obter_convenio_nf ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


cd_convenio_parametro_w		bigint;


BEGIN

begin
select	max(cd_convenio_parametro)
into STRICT	cd_convenio_parametro_w
from (SELECT		a.cd_convenio_parametro
	from		conta_paciente a,
			nota_fiscal b
	where		a.nr_interno_conta = b.nr_interno_conta
	and		b.nr_sequencia = nr_sequencia_p
	and		(b.nr_interno_conta IS NOT NULL AND b.nr_interno_conta::text <> '')
	
union all

	SELECT		a.cd_convenio
	from		protocolo_convenio a,
			nota_fiscal b
	where		a.nr_seq_protocolo = b.nr_seq_protocolo
	and		b.nr_sequencia = nr_sequencia_p
	and		(b.nr_seq_protocolo IS NOT NULL AND b.nr_seq_protocolo::text <> '')
	
union all

	select		a.cd_convenio
	from		lote_protocolo a,
			nota_fiscal b
	where		b.nr_seq_lote_prot	= a.nr_sequencia
	and		b.nr_sequencia		= nr_sequencia_p
	and		(b.nr_seq_lote_prot IS NOT NULL AND b.nr_seq_lote_prot::text <> '')) alias4;

exception
	when no_data_found then
		cd_convenio_parametro_w := null;
end;

return	cd_convenio_parametro_w;

end;
