CREATE OR REPLACE FUNCTION obter_convenio_proc_adic ( nr_seq_proc_interno_p bigint, cd_procedimento_p bigint, nr_seq_agenda_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_convenio_w	integer := 0;
cd_categoria_w	varchar(10);


BEGIN

if (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') then
	select	max(a.cd_convenio),
		max(a.cd_categoria)
	into STRICT	cd_convenio_w,
		cd_categoria_w
	from	agenda_paciente_proc a,
		agenda_paciente b
	where	a.nr_sequencia		=	b.nr_sequencia
	and	a.nr_seq_proc_interno	=	nr_seq_proc_interno_p
	and	a.nr_sequencia		=	nr_seq_agenda_p;
elsif (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then
	select	max(a.cd_convenio),
		max(a.cd_categoria)
	into STRICT	cd_convenio_w,
		cd_categoria_w
	from	agenda_paciente_proc a,
		agenda_paciente b
	where	a.nr_sequencia		=	b.nr_sequencia
	and	a.cd_procedimento	=	cd_procedimento_p
	and	a.nr_sequencia		=	nr_seq_agenda_p;
end	if;

if (ie_opcao_p = 'CO') then
	return	cd_convenio_w;
else
	return	cd_categoria_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
