CREATE OR REPLACE FUNCTION obter_convenio_quimioterapia (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_convenio_w		varchar(255);
nr_atendimento_w	bigint;
nr_prescricao_w		bigint;
cd_convenio_w		integer;



/*
ie_opcao_p
 C - CÃ³digo
 N - Nome
*/
BEGIN

select	nr_atendimento,
	nr_prescricao
into STRICT	nr_atendimento_w,
	nr_prescricao_w
from	can_ordem_prod
where	nr_sequencia	= nr_sequencia_p;


if (coalesce(nr_atendimento_w::text, '') = '') then

	select	nr_atendimento
	into STRICT	nr_atendimento_w
	from	prescr_medica
	where	nr_prescricao	= nr_prescricao_w;
end if;

if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then

	select	substr(obter_convenio_atendimento(nr_atendimento_w),1,50)
	into STRICT	cd_convenio_w
	;

	select	ds_convenio
	into STRICT	ds_convenio_w
	from	convenio
	where	cd_convenio	= cd_convenio_W;

	if (ie_opcao_p	= 'C') then
		ds_retorno_w	:= cd_convenio_w;
	elsif (ie_opcao_p	= 'N') then
		ds_retorno_w	:= ds_convenio_w;
	end if;
end if;

RETURN ds_retorno_w;

END;
$body$
