CREATE OR REPLACE FUNCTION obter_convenio_regra_atend ( cd_medico_p text, cd_convenio_p bigint, ie_tipo_atendimento_p bigint, cd_estabelecimento_p bigint, ie_atend_exec_p text, cd_categoria_p text, cd_plano_convenio_p text) RETURNS varchar AS $body$
DECLARE


ie_atendimento_w		varchar(1);
ie_execucao_w		varchar(1);
ie_exec_partic_w		varchar(1);
ie_tipo_atendimento_w	smallint;
ie_retorno_w		varchar(1) := 'S';
ie_existe_w		bigint;
ie_credenciado_w		varchar(01);

c01 CURSOR FOR
	SELECT	ie_atendimento,
		ie_tipo_atendimento
	from	convenio_regra_medico
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	cd_convenio		= cd_convenio_p
	and	ie_atendimento	= 'S'
	and (ie_tipo_atendimento	= ie_tipo_atendimento_p or coalesce(ie_tipo_atendimento::text, '') = '')
	order by ie_tipo_atendimento desc;

c02 CURSOR FOR
	SELECT	ie_execucao,
		ie_tipo_atendimento
	from	convenio_regra_medico
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	cd_convenio		= cd_convenio_p
	and	ie_execucao		= 'S'
	and (ie_tipo_atendimento	= ie_tipo_atendimento_p or coalesce(ie_tipo_atendimento::text, '') = '')
	order by ie_tipo_atendimento desc;

c03 CURSOR FOR
	SELECT	ie_exec_partic,
		ie_tipo_atendimento
	from	convenio_regra_medico
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	cd_convenio		= cd_convenio_p
