		EXIT WHEN NOT FOUND; /* apply on c01 */

		if (coalesce(ie_tipo_atendimento_w::text, '') = '') or (ie_tipo_atendimento_w = ie_tipo_atendimento_p) then
			if (ie_credenciado_w = 'N') then
				ie_retorno_w	:= 'N';
			end if;
		end if;

		end loop;
	close c01;

elsif (ie_atend_exec_p = 'E') then
