		if (coalesce(ie_tipo_atendimento_w::text, '') = '') or (ie_tipo_atendimento_w = ie_tipo_atendimento_p) then
			if (ie_credenciado_w = 'N') then
				ie_retorno_w	:= 'N';
			end if;
		end if;

		end loop;
	close c02;

elsif (ie_atend_exec_p = 'P') then
	open c03;
		loop
		fetch c03 into
			ie_exec_partic_w,
			ie_tipo_atendimento_w;
		EXIT WHEN NOT FOUND; /* apply on c03 */
		if (coalesce(ie_tipo_atendimento_w::text, '') = '') or (ie_tipo_atendimento_w = ie_tipo_atendimento_p) then
			if (ie_credenciado_w = 'N') then
				ie_retorno_w	:= 'N';
			end if;
		end if;

		end loop;
	close c03;

end if;


return ie_retorno_w;

end;
