SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_convenio_tit_rec ( nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE


cd_convenio_w			integer;
cd_convenio_conta_w		integer;
nr_interno_conta_w		bigint;
nr_titulo_original_w		bigint;
nr_seq_protocolo_w		bigint;
cd_estabelecimento_w		bigint;
cd_cgc_w				varchar(14);


BEGIN

/* Ricardo 01/07/2005 - Mudado a linha abaixo para null
	cd_convenio_w			:= 0; */
cd_convenio_w			:= null;

select	nr_seq_protocolo,
	nr_interno_conta,
	cd_cgc,
	cd_convenio_conta,
	cd_estabelecimento
into STRICT	nr_seq_protocolo_w,
	nr_interno_conta_w,
	cd_cgc_w,
	cd_convenio_conta_w,
	cd_estabelecimento_w
from	Titulo_receber
where	nr_titulo		= nr_titulo_p;

select	max(nr_titulo)
INTO STRICT	nr_titulo_original_w
FROM	titulo_receber_desdob
WHERE	nr_titulo_dest = nr_titulo_p;

if (coalesce(nr_titulo_original_w, 0) > 0) then

	select	nr_seq_protocolo,
		nr_interno_conta,
		cd_cgc,
		cd_convenio_conta
	into STRICT	nr_seq_protocolo_w,
		nr_interno_conta_w,
		cd_cgc_w,
		cd_convenio_conta_w
	from	Titulo_receber
	where	nr_titulo		= nr_titulo_original_w;

end if;

if (nr_interno_conta_w IS NOT NULL AND nr_interno_conta_w::text <> '') then
	select	cd_convenio_parametro
	into STRICT	cd_convenio_w
	from	conta_paciente
	where nr_interno_conta	= nr_interno_conta_w;
elsif (nr_seq_protocolo_w IS NOT NULL AND nr_seq_protocolo_w::text <> '') then
	select	cd_convenio
	into STRICT	cd_convenio_w
	from	Protocolo_convenio
	where nr_seq_protocolo	= nr_seq_protocolo_w;
elsif (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
	select	coalesce(max(a.cd_convenio),0)
	into STRICT	cd_convenio_w
	from	Convenio a,
		convenio_estabelecimento b
	where	a.cd_cgc			= cd_cgc_w
	and	b.cd_convenio		= a.cd_convenio
	and	b.cd_estabelecimento	= cd_estabelecimento_w
	and	a.ie_situacao		= 'A';
end if;

if (coalesce(cd_convenio_w, 0) = 0) then
	cd_convenio_w	:= cd_convenio_conta_w;
end if;

RETURN cd_convenio_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_convenio_tit_rec ( nr_titulo_p bigint) FROM PUBLIC;

