CREATE OR REPLACE FUNCTION obter_convenio_trans_financ (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_titulo_w			bigint;
nr_interno_conta_w		bigint;
nr_seq_protocolo_w		bigint;
cd_convenio_conta_w		bigint;
ds_retorno_w			bigint;


BEGIN 
 
select	nr_seq_titulo_receber 
into STRICT	nr_titulo_w 
from	movto_trans_financ 
where	nr_sequencia = nr_sequencia_p;
 
select	nr_interno_conta, 
	nr_seq_protocolo, 
	cd_convenio_conta 
into STRICT	nr_interno_conta_w, 
	nr_seq_protocolo_w, 
	cd_convenio_conta_w 
from	titulo_receber 
where	nr_titulo = nr_titulo_w;
 
if (coalesce(nr_interno_conta_w,0) <> 0) then 
	select	cd_convenio_parametro 
	into STRICT	ds_retorno_w 
	from	conta_paciente 
	where	nr_interno_conta = nr_interno_conta_w;
elsif (coalesce(nr_seq_protocolo_w,0) <> 0) then 
	select	cd_convenio 
	into STRICT	ds_retorno_w 
	from	protocolo_convenio 
	where	nr_seq_protocolo = nr_seq_protocolo_w;
elsif (coalesce(cd_convenio_conta_w,0) <> 0) then 
	ds_retorno_w := cd_convenio_conta_w;
end if;
 
return ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_convenio_trans_financ (nr_sequencia_p bigint) FROM PUBLIC;

