CREATE OR REPLACE FUNCTION obter_conversao_301 ( nm_tabela_301_p text, nm_tabela_tasy_p text, cd_dominio_tasy_p bigint, ds_valor_p text, ie_conversao_p text default 'E') RETURNS varchar AS $body$
DECLARE


/*ie_conversao_p
I - com base no valor do Tasy, retorna o valor do 301
E - com base no valor do 301, retorna o valor do Tasy*/
ds_retorno_w	varchar(255);

BEGIN

if (ie_conversao_p = 'I') then

	if (cd_dominio_tasy_p IS NOT NULL AND cd_dominio_tasy_p::text <> '') then
		begin
		select	ds_valor_301
		into STRICT	ds_retorno_w
		from	c301_conversao_dados
		where	nm_tabela_301 	= nm_tabela_301_p
		and	cd_dominio_tasy	= cd_dominio_tasy_p
		and	ds_valor_tasy 	= ds_valor_p  LIMIT 1;
		exception
		when others then
			ds_retorno_w := null;
		end;
	else
		begin
		select	ds_valor_301
		into STRICT	ds_retorno_w
		from	c301_conversao_dados
		where	nm_tabela_301 	= nm_tabela_301_p
		and	nm_tabela_tasy 	= nm_tabela_tasy_p
		and	ds_valor_tasy 	= ds_valor_p  LIMIT 1;
		exception
		when others then
			ds_retorno_w := null;
		end;
	end if;

elsif (ie_conversao_p = 'E') then

	if (cd_dominio_tasy_p IS NOT NULL AND cd_dominio_tasy_p::text <> '') then
		begin
		select	ds_valor_tasy
		into STRICT	ds_retorno_w
		from	c301_conversao_dados
		where	nm_tabela_301 	= nm_tabela_301_p
		and	cd_dominio_tasy	= cd_dominio_tasy_p
		and	ds_valor_301 	= ds_valor_p  LIMIT 1;
		exception
		when others then
			ds_retorno_w := null;
		end;
	else
		begin
		select	ds_valor_tasy
		into STRICT	ds_retorno_w
		from	c301_conversao_dados
		where	nm_tabela_301 	= nm_tabela_301_p
		and	nm_tabela_tasy 	= nm_tabela_tasy_p
		and	ds_valor_301 	= ds_valor_p  LIMIT 1;
		exception
		when others then
			ds_retorno_w := null;
		end;
	end if;

end if;

return	ds_retorno_w;

end;
