CREATE OR REPLACE FUNCTION obter_conversao_externa ( cd_cgc_p text, nm_tabela_p text, nm_atributo_p text, cd_interno_p text) RETURNS varchar AS $body$
DECLARE


cd_externo_w			varchar(80);


BEGIN
cd_externo_w			:= '';
begin
select	cd_externo
into STRICT	cd_externo_w
from	conversao_meio_externo
where	((coalesce(cd_cgc_p::text, '') = '') or (cd_cgc = cd_cgc_p))
and	upper(nm_tabela)	= upper(nm_tabela_p)
and	upper(nm_atributo)	= upper(nm_atributo_p)
and	cd_interno		= cd_interno_p
and	coalesce(ie_envio_receb,'A') in ('E','A');
exception
    		when others then
		cd_externo_w := '';
end;

if (coalesce(cd_externo_w::text, '') = '')	or (cd_externo_w = '')	then
	cd_externo_w	:= cd_interno_p;
end if;

RETURN cd_externo_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
