CREATE OR REPLACE FUNCTION obter_conversao_interna ( cd_cgc_p text, nm_tabela_p text, nm_atributo_p text, cd_externo_p text) RETURNS varchar AS $body$
DECLARE


cd_interno_w			varchar(80) := '';


BEGIN

begin

select	max(cd_interno)
into STRICT	cd_interno_w
from	conversao_meio_externo
where	((coalesce(cd_cgc_p::text, '') = '') or (cd_cgc = cd_cgc_p))
and	upper(nm_tabela)	= upper(nm_tabela_p)
and	upper(nm_atributo)	= upper(nm_atributo_p)
and	upper(cd_externo)	= upper(cd_externo_p)
and	coalesce(ie_envio_receb,'A') in ('R','A');
exception
    		when others then
		cd_interno_w := '';
end;

return cd_interno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
