CREATE OR REPLACE FUNCTION obter_conversao_mat_onco ( cd_material_p bigint, cd_material_alterado_p bigint, nr_seq_atendimento_p bigint, nr_seq_interno_p bigint, qt_autorizada_p bigint) RETURNS bigint AS $body$
DECLARE


qt_conversao_dose_w		double precision;
qt_dose_autorizada_w		double precision := 0;
cd_unid_med_prescr_w		varchar(30);
cd_unid_med_consumo_alt_w	varchar(30);


BEGIN

Select	max(cd_unid_med_prescr)
into STRICT	cd_unid_med_prescr_w
from	paciente_atend_medic
where	nr_seq_atendimento = nr_seq_atendimento_p
and	nr_seq_interno_p = nr_seq_interno_p
and	cd_material = cd_material_p;


select	max(CD_UNIDADE_MEDIDA_CONSUMO)
into STRICT	cd_unid_med_consumo_alt_w
from	material
where	cd_material	= cd_material_alterado_p;


IF (cd_unid_med_consumo_alt_w = cd_unid_med_prescr_w) THEN
	qt_conversao_dose_w	:= 1;
else
	begin

	select	coalesce(max(qt_conversao),1)
	into STRICT	qt_conversao_dose_w
	from	material_conversao_unidade
	where	cd_material 	= cd_material_alterado_p
	and	cd_unidade_medida = cd_unid_med_prescr_w;

	exception
		when others then
		qt_conversao_dose_w	:= 1;
	end;
end if;


qt_dose_autorizada_w := coalesce((qt_autorizada_p * qt_conversao_dose_w),0);


return qt_dose_autorizada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
