CREATE OR REPLACE FUNCTION obter_conversao_mat_sem_apres ( nr_prescricao_p bigint, nr_seq_material_p bigint, cd_material_sem_apres_p bigint, cd_unidade_medida_p text, nr_seq_atend_medic_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w			double precision;
qt_dose_w			double precision;
cd_unidade_medida_dose_w	varchar(30);
cd_material_w			bigint;

c01 CURSOR FOR
SELECT	qt_dose,
	cd_unidade_medida_dose,
	cd_material
from	prescr_material
where	nr_prescricao				= nr_prescricao_p
and	((nr_sequencia				= nr_seq_material_p) or
	 ((coalesce(nr_seq_material_p,0)		= 0) and (nr_seq_atend_medic			= nr_seq_atend_medic_p)))
and	coalesce(cd_mat_sem_apresent, cd_material)	= cd_material_sem_apres_p;


BEGIN

begin
open c01;
loop
fetch c01 into
	qt_dose_w,
	cd_unidade_medida_dose_w,
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	if (cd_unidade_medida_dose_w	= cd_unidade_medida_p) then
		vl_retorno_w	:= coalesce(vl_retorno_w,0) + coalesce(qt_dose_w,0);
	else
		vl_retorno_w	:= coalesce(vl_retorno_w,0) + (coalesce(obter_conversao_unid_med_cons(cd_material_w, cd_unidade_medida_dose_w, qt_dose_w),1) *
							  coalesce(obter_conversao_unid_med(cd_material_w, cd_unidade_medida_p),1));
	end if;
end loop;
close c01;
exception when others then
	-- Houve problema com a convers√£o da dose. Verifique o peso e a dose informada!
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(200288);
end;

return	vl_retorno_w;

end;
$body$
