CREATE OR REPLACE FUNCTION obter_conversao_meio_ext_ipe ( cd_cgc_convenio_p text, nm_tabela_p text, nm_atributo_p text, cd_interno_p text) RETURNS varchar AS $body$
DECLARE

cd_externo_w	varchar(40);

BEGIN

begin
select	max(cd_externo)
into STRICT	cd_externo_w
from	conversao_meio_externo
where	cd_cgc 			= cd_cgc_convenio_p
and	upper(nm_tabela) 	= upper(nm_tabela_p)
and	upper(nm_atributo) 	= upper(nm_atributo_p)
and	cd_interno 		= cd_interno_p;
exception
when others then
	cd_externo_w := null;
end;

return	cd_externo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
