CREATE OR REPLACE FUNCTION obter_conversao_meio_ext ( nr_seq_regra_p bigint, cd_cgc_p text, nm_tabela_p text, nm_atributo_p text, ie_operacao_p text, cd_origem_p text) RETURNS varchar AS $body$
DECLARE


/* IE_OPERACAO_P
E = Busca o código externo, através do cd_interno
I = Busca o código interno, através do CD_EXTERNO
*/
cd_conversao_w			varchar(80);
nm_atributo_w			varchar(50);
nm_tabela_w			varchar(50);


BEGIN

cd_conversao_w	:= '';
nm_atributo_w	:= upper(nm_atributo_p);
nm_tabela_w	:= upper(nm_tabela_p);

if (ie_operacao_p = 'E') then
	begin

	select	cd_externo
	into STRICT	cd_conversao_w
	from	conversao_meio_externo
	where	nr_seq_regra	= nr_seq_regra_p
	and	nm_tabela	= nm_tabela_w
	and	nm_atributo	= nm_atributo_w
	and	cd_interno	= cd_origem_p;
	exception when others then
		cd_conversao_w := '';
	end;
elsif (ie_operacao_p = 'I') then
	begin

	select	cd_interno
	into STRICT	cd_conversao_w
	from	conversao_meio_externo
	where	nr_seq_regra	= nr_seq_regra_p
	and	nm_tabela	= nm_tabela_w
	and	nm_atributo	= nm_atributo_w
	and	cd_externo	= cd_origem_p;
	exception when others then
		cd_conversao_w := '';
	end;
end if;

return cd_conversao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
