CREATE OR REPLACE FUNCTION obter_conversao_ml_sne (cd_material_p bigint, qt_dose_p bigint, cd_unid_med_p text) RETURNS bigint AS $body$
DECLARE



qt_retorno_w			double precision;
qt_conversao_w			double precision;
cd_unidade_medida_w		varchar(30);
cd_unid_med_cons_w		varchar(30);


BEGIN
cd_unidade_medida_w	:= upper(cd_unid_med_p);

if (upper(cd_unidade_medida_w) = upper(obter_unid_med_usua('ML'))) then
	qt_retorno_w	:= qt_dose_p;
else
	cd_unid_med_cons_w	:= upper(substr(obter_dados_material_estab(cd_material_p, wheb_usuario_pck.get_cd_estabelecimento, 'UMS'),1,30));
	select	coalesce(max(qt_conversao),0)
	into STRICT	qt_conversao_w
	from	material_conversao_unidade
	where	cd_material = cd_material_p
	and		upper(cd_unidade_medida) = upper(obter_unid_med_usua('ML'));

	if (cd_unidade_medida_w = cd_unid_med_cons_w) then
		qt_retorno_w	:= dividir(qt_dose_p,qt_conversao_w);
	else
		select	coalesce(max(qt_conversao),0)
		into STRICT	qt_conversao_w
		from	material_conversao_unidade
		where	cd_material = cd_material_p
		and		upper(cd_unidade_medida) = cd_unidade_medida_w;

		qt_retorno_w	:= qt_dose_p * qt_conversao_w;
	end if;

end if;

return qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
