CREATE OR REPLACE FUNCTION obter_conversao_moeda (vl_converter_p bigint, cd_moeda_converter_p bigint, dt_cotacao_p timestamp, ie_tipo_conversao_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w	cotacao_moeda.vl_cotacao%type;
vl_cotacao_w	cotacao_moeda.vl_cotacao%type;


BEGIN

select	max(vl_cotacao)
into STRICT	vl_cotacao_w
from	moeda a,
	cotacao_moeda b
where	a.cd_moeda 	= b.cd_moeda
and	a.cd_moeda 	= cd_moeda_converter_p
and	b.dt_cotacao 	= (SELECT	max(x.dt_cotacao)
	   		   from		cotacao_moeda x
	   		   where	x.cd_moeda = a.cd_moeda
			   and		x.dt_cotacao <= fim_dia(dt_cotacao_p));

/*Moeda estrangeira para nacional*/

if (ie_tipo_conversao_p = 'EN') then
	vl_retorno_w	:=	coalesce(vl_converter_p * vl_cotacao_w,0);

/*Moeda nacional para estrangeira*/

elsif (ie_tipo_conversao_p = 'NE') then
	vl_retorno_w	:=	coalesce(dividir_sem_round(vl_converter_p,vl_cotacao_w),0);
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_conversao_moeda (vl_converter_p bigint, cd_moeda_converter_p bigint, dt_cotacao_p timestamp, ie_tipo_conversao_p text) FROM PUBLIC;

