CREATE OR REPLACE FUNCTION obter_conversao_unidade_nais (nr_prescricao_p prescr_material.nr_prescricao%type, nr_seq_solucao_p prescr_material.nr_sequencia%type, qtd_consumo_p bigint) RETURNS bigint AS $body$
DECLARE


cd_material_w material.cd_material%type;
cd_unidade_medida_dose_w material.cd_unidade_medida_consumo%type;
cd_unid_medida_nais_w material.cd_unid_medida_nais%type;
unidade_convertida_w bigint;


BEGIN

	if (coalesce(nr_prescricao_p,0) > 0) then


		select	max(m.cd_material),
				max(pm.cd_unidade_medida_dose)
		into STRICT	cd_material_w,
				cd_unidade_medida_dose_w
		from	prescr_material pm,
				material m
		where 	pm.nr_prescricao = nr_prescricao_p
		and 	pm.nr_sequencia = nr_seq_solucao_p
		and 	pm.cd_material = m.cd_material;

		if (coalesce(cd_material_w,0) > 0) then

			unidade_convertida_w := ceil(obter_conversao_unid_med_cons(cd_material_w, cd_unidade_medida_dose_w, qtd_consumo_p));

		end if;

	end if;

return unidade_convertida_w;

end	;
