CREATE OR REPLACE FUNCTION obter_conver_marca_mat (cd_material_p bigint, nr_seq_marca_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_conv_compra_est_w		double precision;


BEGIN

if (cd_material_p > 0) and (nr_seq_marca_p > 0) then
	select	coalesce(qt_conv_compra_est,0)
	into STRICT	qt_conv_compra_est_w
	from	material_marca
	where	nr_sequencia = nr_seq_marca_p
	and	cd_material = cd_material_p;
end if;

return	qt_conv_compra_est_w;

end;
