SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_conv_cat_agenda_quimio (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(10);

/*Conv 	- 	ConvÃªnio
   Cat    	- 	Categoria */
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_opcao_p = 'CONV') then

	select 	max(c.cd_convenio)
	into STRICT	ds_retorno_w
	from	agenda_quimio a,
		agenda_integrada_item b,
		agenda_integrada c,
		mapa_ocupacao_quimio d
	where 	a.nr_seq_pend_agenda	=	b.nr_seq_pend_quimio
	and	b.nr_seq_agenda_int	=	c.nr_sequencia
	and	a.nr_sequencia		=	d.nr_seq_agenda_quimio
	and	d.nr_sequencia		=	nr_sequencia_p;
elsif (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_opcao_p = 'CAT') then

	select 	max(c.cd_categoria)
	into STRICT	ds_retorno_w
	from	agenda_quimio a,
		agenda_integrada_item b,
		agenda_integrada c,
		mapa_ocupacao_quimio d
	where 	a.nr_seq_pend_agenda	=	b.nr_seq_pend_quimio
	and	b.nr_seq_agenda_int	=	c.nr_sequencia
	and	a.nr_sequencia		=	d.nr_seq_agenda_quimio
	and	d.nr_sequencia		=	nr_sequencia_p;
end if;



return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
