SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_conv_cat_atendimento ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_convenio_w			integer;
cd_categoria_w		varchar(10);
ds_retorno_w			varchar(255);


BEGIN

ds_retorno_w			:= Wheb_mensagem_pck.get_texto(308893); --'NÃ£o encontrado';
select coalesce(max(cd_convenio),0),
	coalesce(max(cd_categoria),' ')
into STRICT	cd_convenio_w,
	cd_categoria_w
from 	Atend_categoria_convenio a
where	a.nr_atendimento		= nr_atendimento_p
  and	a.dt_inicio_vigencia	=
	(SELECT max(dt_inicio_vigencia)
	from Atend_categoria_convenio b
	where nr_atendimento	= nr_atendimento_p);

select	ds_convenio || ' ' || ds_categoria
into STRICT	ds_retorno_w
from	categoria_convenio b,
	convenio a
where	a.cd_convenio		= cd_convenio_w
  and	b.cd_convenio		= cd_convenio_w
  and	b.cd_categoria	= cd_categoria_w;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
