CREATE OR REPLACE FUNCTION obter_conv_diluente ( nr_sequencia_p bigint ) RETURNS bigint AS $body$
DECLARE


cd_material_w		integer;
qt_dose_w		double precision;
cd_unidade_medida_w	varchar(30);
qt_dose_ret_w		double precision;


BEGIN

select	cd_material,
	qt_dose,
	cd_unidade_medida
into STRICT	cd_material_w,
	qt_dose_w,
	cd_unidade_medida_w
from	agente_anest_diluicao
where	nr_seq_agente_cir	= nr_sequencia_p
and	ie_origem_inf	= 2;

select	obter_conversao_ml(cd_material_w,qt_dose_w,cd_unidade_medida_w)
into STRICT	qt_dose_ret_w
;

return	qt_dose_ret_w;

end;
$body$
