SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_conv_int_cross (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_retorno_w 			bigint := 0;
ie_tipo_convenio_w 		atendimento_paciente.ie_tipo_convenio%type;
ie_tipo_Atendimento_w	atendimento_paciente.ie_tipo_Atendimento%type;


BEGIN 
 
if (coalesce(nr_atendimento_p,0) > 0) then 
 
	select 	coalesce(max(ie_tipo_convenio),0), 
			coalesce(max(ie_tipo_atendimento), 0) 
	into STRICT	ie_tipo_convenio_w, 
			ie_tipo_Atendimento_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
 
	if (ie_tipo_convenio_w > 0) then 
		select	count(*) 
		into STRICT	nr_retorno_w 
		from	regra_envio_cross 
		where	ie_situacao = 'A' 
		and		coalesce(ie_tipo_convenio, ie_tipo_convenio_w) = ie_tipo_convenio_w 
		and		coalesce(ie_tipo_atendimento, ie_tipo_atendimento_w) = ie_tipo_Atendimento_w;
		 
		if (nr_retorno_w = 0) then	 
			select	CASE WHEN count(*)=0 THEN 1  ELSE 0 END  
			into STRICT	nr_retorno_w 
			from	regra_envio_cross 
			where	ie_situacao = 'A';
		end if;
	end if;
end if;
	 
return nr_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
