SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_conv_ocupacao (nr_atendimento_p bigint, ds_param_239_ocup_p text default null) RETURNS varchar AS $body$
DECLARE

ds_convenio_w 		varchar(255); -- Traz  o convênio.
ds_convenio_glosa_w 	varchar(255); -- Traz o convênio glosa.
cd_convenio_glosa_w	bigint;    -- Código do convênio glosa.
ie_parametro_w		varchar(1);   -- Retorno do parâmetro
ie_retorna_w		varchar(255); -- Retorno da function(Dependendo do ie_parametro_w concatena o convênio e o convênio glosa)
BEGIN
if (coalesce(ds_param_239_ocup_p::text, '') = '') then
	ie_parametro_w := obter_param_usuario(44, 239, Obter_perfil_Ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_parametro_w);
else
	ie_parametro_w := ds_param_239_ocup_p;
end if;


ds_convenio_w	:= substr(Obter_desc_convenio(obter_convenio_atendimento(nr_atendimento_p)),1,80);

if (ie_parametro_w = 'S') then
	select  max(cd_convenio_glosa)
	into STRICT	cd_convenio_glosa_w
	from 	atend_categoria_convenio
	where	nr_atendimento = nr_atendimento_p;

	if (cd_convenio_glosa_w IS NOT NULL AND cd_convenio_glosa_w::text <> '') then
		ds_convenio_glosa_w	:= substr(Obter_desc_convenio(cd_convenio_glosa_w),1,80);
	end if;


	if (ds_convenio_glosa_w IS NOT NULL AND ds_convenio_glosa_w::text <> '') then
		ie_retorna_w := ds_convenio_w||' / '||coalesce(ds_convenio_glosa_w,'-');
	else
		ie_retorna_w := ds_convenio_w;
	end if;
elsif (ie_parametro_w = 'N') then
	begin
	ie_retorna_w := ds_convenio_w;
	end;
end if;



return	ie_retorna_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
