CREATE OR REPLACE FUNCTION obter_conv_pf_atend (cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
nr_atendimento_w	bigint;
cd_convenio_w	integer;
ds_convenio_w	varchar(255);
ds_retorno_w	varchar(255);

/* 
ie_opcao_p 
'D' - Descricao 
'C' - Codigo 
*/
 
 

BEGIN 
 
select	max(nr_atendimento) 
into STRICT	nr_atendimento_w 
from	atendimento_paciente 
where	cd_pessoa_fisica =	cd_pessoa_fisica_p;
 
select	max(cd_convenio), 
	obter_nome_convenio(max(cd_convenio)) 
into STRICT	cd_convenio_w, 
	ds_convenio_w 
from	atend_categoria_convenio 
where	nr_atendimento	= nr_atendimento_w 
and	nr_seq_interno	= obter_atecaco_atendimento(nr_atendimento_w) 
group by	cd_convenio;
 
if (ie_opcao_p = 'D') then 
	ds_retorno_w	:= ds_convenio_w;
elsif (ie_opcao_p = 'C') then 
	ds_retorno_w	:= cd_convenio_w;
end if;
 
return ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
