CREATE OR REPLACE FUNCTION obter_conv_porte_conversao ( cd_convenio_p bigint, nr_cirurgia_p bigint) RETURNS bigint AS $body$
DECLARE


ie_porte_w		varchar(1);
qt_porte_anest_w	smallint	:= 99;


BEGIN

if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') and (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') then

	select	ie_porte
	into STRICT	ie_porte_w
	from	cirurgia
	where	nr_cirurgia	= nr_cirurgia_p;

	if (ie_porte_w IS NOT NULL AND ie_porte_w::text <> '') then

		select	max(qt_porte_anest)
		into STRICT	qt_porte_anest_w
		from	conv_porte_conversao
		where	cd_convenio 	= cd_convenio_P
		and	ie_porte	= ie_porte_w;

	end if;

end if;

return	qt_porte_anest_w;

end;
$body$
