SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_conv_receb ( nm_tabela_p text, nm_atributo_p text, cd_sistema_codificacao_p text, cd_externo_p text ) RETURNS varchar AS $body$
DECLARE


ds_value_w		varchar(4000);


BEGIN

select	cd_interno
into STRICT	ds_value_w
from	conversao_meio_externo
where	upper(nm_tabela)	= upper(nm_tabela_p)
and	upper(nm_atributo)	= upper(nm_atributo_p)
and (
	(coalesce(cd_sistema_codificacao_p::text, '') = '')
	or (upper(cd_sistema_codificacao) = upper(cd_sistema_codificacao_p))
)
and	upper(cd_externo)	= upper(cd_externo_p)
and	coalesce(ie_envio_receb,'A') in ('R','A')
 LIMIT 1;

return ds_value_w;

exception
	when others then
		return null;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
