SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_conv_regra_alerta (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_estabelecimento_w		smallint;
cd_convenio_w			integer;
cd_categoria_w			varchar(10);
ds_observacao_w			varchar(255) := '';
cd_empresa_w			bigint;
ie_tipo_atendimento_w		smallint;

C01 CURSOR FOR 
	SELECT	substr(ds_observacao,1,255) 
	from	conv_regra_alerta 
	where	cd_estabelecimento 				= cd_estabelecimento_w 
	and	cd_convenio					= cd_convenio_w 
	and	coalesce(cd_categoria, coalesce(cd_categoria_w,'0'))	= coalesce(cd_categoria_w,'0') 
	and	coalesce(cd_empresa, coalesce(cd_empresa_w,0))	= coalesce(cd_empresa_w,0) 
	and	coalesce(ie_tipo_atendimento, coalesce(ie_tipo_atendimento_w,0))	= coalesce(ie_tipo_atendimento_w,0) 
	and	coalesce(cd_perfil, coalesce(obter_perfil_ativo,0)) = coalesce(obter_perfil_ativo,0) 
	order by	coalesce(cd_categoria,'0'), 
			coalesce(cd_empresa,0), 
			coalesce(ie_tipo_atendimento_w,0), 
			coalesce(cd_perfil,0);
			

BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	 
	select	max(a.cd_estabelecimento), 
		max(b.cd_convenio), 
		max(b.cd_categoria), 
		max(b.cd_empresa), 
		max(a.ie_tipo_atendimento) 
	into STRICT	cd_estabelecimento_w, 
		cd_convenio_w, 
		cd_categoria_w, 
		cd_empresa_w, 
		ie_tipo_atendimento_w 
	from	atendimento_paciente 		a, 
		atend_categoria_convenio	b 
	where	a.nr_atendimento = nr_atendimento_p 
	and	a.nr_atendimento = b.nr_atendimento;
	 
	open C01;
	loop 
	fetch C01 into	 
		ds_observacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		ds_observacao_w := ds_observacao_w;
		end;
	end loop;
	close C01;
	 
end if;
 
return	ds_observacao_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
