CREATE OR REPLACE FUNCTION obter_copias_convenio (cd_convenio_p text) RETURNS varchar AS $body$
DECLARE

nr_copias_w 	varchar(10);

BEGIN

	if (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') then
		select max(nr_copias)
		into STRICT	nr_copias_w
		from 	convenio_regra_copia_laudo
		where 	cd_convenio = cd_convenio_p;
	end if;

return	nr_copias_w;

end;
$body$
LANGUAGE PLPGSQL
