SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_copias_regra_pep ( cd_convenio_p bigint) RETURNS bigint AS $body$
DECLARE


nr_resultado_w			integer;
cd_empresa_w			smallint;
cd_estabelecimento_w	smallint;


BEGIN

cd_estabelecimento_w	:= coalesce(wheb_usuario_pck.get_cd_estabelecimento,0);
cd_empresa_w			:= coalesce(OBTER_EMPRESA_ESTAB(cd_estabelecimento_w),0);

select	coalesce(max(nr_copias),0)
into STRICT	nr_resultado_w
from	convenio_regra_copia_pep
where	cd_convenio = cd_convenio_p
and		coalesce(cd_empresa,cd_empresa_w)  = cd_empresa_w
and		coalesce(cd_estabelecimento,cd_estabelecimento_w)	= cd_estabelecimento_w
and		coalesce(ie_regra,'C') = 'C'
and		coalesce(NR_SEQ_ITEM,385) = 385
order by coalesce(cd_empresa,0),
		 coalesce(cd_estabelecimento,0);


return	nr_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
