CREATE OR REPLACE FUNCTION obter_cor_autor_agenda (ie_autorizacao_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_cor_w	varchar(25);
ds_cor_fundo_w 	varchar(25);
ds_cor_fonte_w 	varchar(25);


BEGIN

if (ie_autorizacao_p IS NOT NULL AND ie_autorizacao_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then
	begin

	select	max(ds_cor_fonte),
		max(ds_cor_fundo)
	into STRICT	ds_cor_fonte_w,
		ds_cor_fundo_w
	from	agenda_autor_cor
	where	ie_autorizacao = ie_autorizacao_p;

	if (ie_opcao_p = 'B') then
		ds_cor_w := ds_cor_fundo_w;
	elsif (ie_opcao_p = 'F') then
		ds_cor_w := ds_cor_fonte_w;
	end if;
	end;
end if;

return	ds_cor_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
