CREATE OR REPLACE FUNCTION obter_cor_aviso_agenda ( cd_agenda_p bigint, hr_inicio_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_cor_w	varchar(15);
ie_dia_semana_w	varchar(2);

BEGIN

if (hr_inicio_p IS NOT NULL AND hr_inicio_p::text <> '') then
	ie_dia_semana_w := Obter_Cod_Dia_Semana(hr_inicio_p);
	select	coalesce(max(ds_cor_aviso),'$00CEE6B7')
	into STRICT	ds_cor_w
	from	agenda_horario_aviso
	where	hr_inicio_p >= dt_inicial
	and 	hr_inicio_p <  dt_final
	and	cd_agenda = cd_agenda_p
	and 	((ie_dia_semana  = ie_dia_semana_w) or (coalesce(ie_dia_semana::text, '') = '') or ((ie_dia_semana = 9) and (ie_dia_semana_w not in (7,1))))
	and	nr_sequencia =  (	SELECT 	max(x.nr_sequencia)
				from   	agenda_horario_aviso x
				where	hr_inicio_p >= x.dt_inicial
				and 	hr_inicio_p <  x.dt_final
				and	x.cd_agenda =  cd_agenda_p
				and 	((x.ie_dia_semana  = ie_dia_semana_w) or (coalesce(x.ie_dia_semana::text, '') = '') or ((x.ie_dia_semana = 9) and (ie_dia_semana_w not in (7,1))))
				and 	((coalesce(ie_considera_entre_hor,'N') <> 'S') or ((coalesce(ie_considera_entre_hor,'N') = 'S') and (to_date(to_char(clock_timestamp(), 'dd/mm/yyyy') ||' '|| to_char(hr_inicio_p, 'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') >=
					to_date(to_char(clock_timestamp(), 'dd/mm/yyyy') ||' '|| to_char(dt_inicial, 'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')) and (to_date(to_char(clock_timestamp(), 'dd/mm/yyyy') ||' '|| to_char(hr_inicio_p, 'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') <
					to_date(to_char(clock_timestamp(), 'dd/mm/yyyy') ||' '|| to_char(dt_final, 'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')))))
	and 	((coalesce(ie_considera_entre_hor,'N') <> 'S') or ((coalesce(ie_considera_entre_hor,'N') = 'S') and (to_date(to_char(clock_timestamp(), 'dd/mm/yyyy') ||' '|| to_char(hr_inicio_p, 'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') >=
		to_date(to_char(clock_timestamp(), 'dd/mm/yyyy') ||' '|| to_char(dt_inicial, 'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')) and (to_date(to_char(clock_timestamp(), 'dd/mm/yyyy') ||' '|| to_char(hr_inicio_p, 'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') <
		to_date(to_char(clock_timestamp(), 'dd/mm/yyyy') ||' '|| to_char(dt_final, 'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss'))));

end if;

return ds_cor_w;

end;
