CREATE OR REPLACE FUNCTION obter_cor_barras_atepacfq ( nr_seq_cor_p bigint, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_cor_w		varchar(15) :='';
ds_cor_perfil_w		varchar(15) :='';
ds_retorno_w		varchar(15);


BEGIN

select	ds_cor_fundo
into STRICT	ds_cor_w
from	tasy_padrao_cor
where	nr_sequencia = nr_seq_cor_p;

select	coalesce(max(ds_cor_fundo),'X')
into STRICT	ds_cor_perfil_w
from	tasy_padrao_cor_perfil
where	nr_seq_padrao = nr_seq_cor_p
and	cd_estabelecimento = cd_estabelecimento_p
and	((cd_perfil = cd_perfil_p) or (coalesce(cd_perfil,0) = 0));

if (ds_cor_perfil_w <> 'X') then
	ds_retorno_w := ds_cor_perfil_w;
else
	ds_retorno_w := ds_cor_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
