CREATE OR REPLACE FUNCTION obter_cor_bsc ( dt_periodo_p timestamp, cd_mes_p text, cd_ano_p text, ds_retorno_campo_p bigint, nr_valor_padrao_p bigint) RETURNS bigint AS $body$
DECLARE


ds_valor_w	varchar(255);


BEGIN

select 	MAX(CASE WHEN TRUNC(dt_periodo_p,'mm')=TRUNC(add_months(to_date('01' || LPAD(cd_mes_p, 2, '0') || cd_ano_p),-1),'mm') THEN ds_retorno_campo_p  ELSE nr_valor_padrao_p END )
into STRICT	ds_valor_w
;

return	ds_valor_w;

end;
$body$
LANGUAGE PLPGSQL
