CREATE OR REPLACE FUNCTION obter_cor_cadastro ( nr_seq_cor_p bigint, cd_perfil_p bigint, cd_estabelecimento_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


/*
1	= ie_opcao_p - cor de fundo
2	= ie_opcao_p - cor da fonte
3	= ie_opcao_p - cor da seleção
*/
ds_cor_fundo_w varchar(15);
ds_cor_fonte_w varchar(15);
ds_cor_selecao_w varchar(15);
ds_retorno_w varchar(15);


BEGIN

SELECT * FROM Tasy_Obter_Cor(nr_seq_cor_p, cd_perfil_p, cd_estabelecimento_p, ds_cor_fundo_w, ds_cor_fonte_w, ds_cor_selecao_w) INTO STRICT ds_cor_fundo_w, ds_cor_fonte_w, ds_cor_selecao_w;

if (ie_opcao_p = 1) then
	ds_retorno_w := ds_cor_fundo_w;
elsif (ie_opcao_p = 2) then
	ds_retorno_w := ds_cor_fonte_w;
elsif (ie_opcao_p = 3) then
	ds_retorno_w := ds_cor_selecao_w;
end if;

return ds_retorno_w;

end;
$body$
